properties.OnSelect:
Set(VADVZuBearbeiten, ThisItem.Titel);
Set(VTyp, "AN");

Set(VMyAdv, false);

Set(VName, ThisItem.Kundenname);
Set(VOrt, ThisItem.'Kunden Anschrift Ort');
Set(VPLZ, ThisItem.'Kunden Anschrift PLZ');
Set(VLand, ThisItem.'Kunden Anschrift Land');
Set(VNummer, ThisItem.'Kunden Anschrift Nummer');
Set(VStraße, ThisItem.'Kunden Anschrift Straße');
Set(VVertreter, ThisItem.'Kunden Vertreter');
Set(VPosVertreter, ThisItem.'Kunden Vertreter Position');
Set(VADVIndex, Int(ThisItem.Titel));

Set(
        VTempRecord,
        LookUp(
            'ADV Auftragnehmer',
            Titel = Text(VADVIndex)
        )
    );

ClearCollect(
    KundendatenCollection,
        {Name: If(IsBlank(VName),"Name",VName), Ort: If(IsBlank(VOrt),"Ort",VOrt), PLZ: If(IsBlank(VPLZ),"PLZ",VPLZ), Land: If(IsBlank(VLand),"Land",VLand), Straße: If(IsBlank(VStraße),"Straße",VStraße), Vertreter: If(IsBlank(VVertreter),"Vertreter",VVertreter), PosVertreter: If(IsBlank(VPosVertreter),"Position Vertreter",VPosVertreter),  Nummer: If(IsBlank(VNummer),"Nummer",VNummer)}
);

ClearCollect(
        VANCollection,
        {Paragraph:1, Absatz:2, Inhalt:ThisItem.'§1 2.', Name: "Gegenstand des Auftrags"},
        {Paragraph:1, Absatz:3, Inhalt:ThisItem.'§1 3.', Name: "Dauer des Auftrags"},
        {Paragraph:1, Absatz:4, Inhalt:ThisItem.'§1 4.', Name: "Art und Zweck der Verarbeitung von Daten"},
        {Paragraph:1, Absatz:6, Inhalt:ThisItem.'§1 6.', Name: "Art der Daten"},
        {Paragraph:1, Absatz:7, Inhalt:ThisItem.'§1 7.', Name: "Kategorien betroffener Personen"},
        {Paragraph:6, Absatz:1, Inhalt:ThisItem.'§6 1.', Name: "Weisungsrechte"},
        {Paragraph:6, Absatz:2, Inhalt:ThisItem.'§6 2.', Name: "Weisungsrechte"}
);
Set(VCol, VANCollection);

// Step 1: Initialize ColUAV with the correct schema
ClearCollect(
    ColUAV,
    Table(
        {
            Index: Blank(),
            Name: Blank(),
            Straße: Blank(),
            PLZ_ORT: Blank(),
            Adresszusatz: Blank(),
            tätigkeit: Blank(),
            isEmpty: Blank()
        }
    )
);

Set(parsedJSON, ParseJSON(ThisItem.__unterauftragsverarbeiter));

// Step 2: Parse the JSON and store it in a variable
Set(parsedJSON, ParseJSON(ThisItem.__unterauftragsverarbeiter));

// Step 3: Populate ColUAV with the parsed JSON data
ForAll(
    parsedJSON,
    Collect(
        ColUAV,
        {
            Index: Int(ThisRecord.Index),
            Name: Text(ThisRecord.Name),
            Straße: Text(ThisRecord.Straße),
            PLZ_ORT: Text(ThisRecord.PLZ_ORT),
            Adresszusatz: Text(ThisRecord.Adresszusatz),
            tätigkeit: Text(ThisRecord.tätigkeit),
            isEmpty: Boolean(ThisRecord.isEmpty)
        }
    )
);

// Step 4: Remove the initial blank record from ColUAV
If(CountRows(ColDSGVO)>1,
    RemoveIf(
        ColUAV,
        IsBlank(Index) && IsBlank(Name) && IsBlank(Straße) && IsBlank(PLZ_ORT) && IsBlank(Adresszusatz) && IsBlank(tätigkeit)
    )
);

// Step 1: Initialize ColDSGVO with the correct schema
ClearCollect(
    ColDSGVO,
    Table(
